import React, {Component} from 'react';
import {omsUrl} from './getOmsOfferData';
import { Badge } from 'react-bootstrap';

class OfferList extends Component {
    state = {
      offers: []
    }

    componentDidMount() {
      let url = `${omsUrl}`;

      fetch(url)
      .then(data => data.json())
      .then(data => {
        this.setState({
          offers: data.Table
        })
      })
      .catch(err => {
        console.log('======failure=======');
        console.log(err);
      });


    }

      render() {
        let offerData = this.state.offers.map((offer, index) => {
          return(
            <div className="position-relative">
              <h4>{offer.title}</h4>
              <p>{offer.name}</p>
              <p>{offer.store_number}</p>
              <p>{offer.street}</p>
              <p>{offer.city}</p>
              <p>{offer.state}</p>
              <p>{offer.zip}</p>
              <p>{offer.latitude}</p>
              <p>{offer.longitude}</p>
              <span className="d-block pb-2 mb-0 h6 text-uppercase text-info font-weight-bold">
            Editor's Pick
            <Badge pill color="success" className="text-uppercase px-2 py-1 ml-3 mb-1 align-middle" style={{ fontSize: '0.75rem' }}>New</Badge>
          </span>

          <article className="pt-5 text-secondary text-justify" style={{ fontSize: '0.9rem', whiteSpace: 'pre-line' }}>{this.state.post}</article>
            </div>
          )
        })

        return (
          <article>
            <h2>OMS Offer List</h2>
            {offerData}
          </article>
        );
      }
    }

    export default OfferList;
